mapper.select:
SELECT group_id,group_step,group_tab 
FROM qnareply
WHERE no=#{no}

mapper.update:
UPDATE qnareply SET
group_step=group_step+1 
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE qnareply SET
depth=depth+1
WHERE no=#{no}

UPDATE qnareply SET
msg='관리자가 삭제한 댓글입니다'
WHERE no=#{no}

UPDATE qnareply SET
depth=depth-1
WHERE no=#{no}

UPDATE qnareply SET
msg=#{msg}
WHERE no=#{no}
